BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "conjunction_table" (
	"disaster_id"	TEXT NOT NULL UNIQUE,
	"time_id"	TEXT NOT NULL UNIQUE,
	PRIMARY KEY("disaster_id"),
	FOREIGN KEY("time_id") REFERENCES "conjunction_table"("time_id") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "natural_disasters" (
	"Event Disaster ID"	TEXT NOT NULL UNIQUE,
	"Event Name"	TEXT NOT NULL,
	"Countries affected"	TEXT,
	"Event Disaster Type"	TEXT,
	"Event Death toll (Estimate)"	INTEGER,
	PRIMARY KEY("Event Disaster ID")
);
CREATE TABLE IF NOT EXISTS "time" (
	"time_id"	TEXT NOT NULL UNIQUE,
	"Date (From)"	TEXT NOT NULL,
	"Date (To)"	TEXT,
	"Note"	TEXT,
	"disaster_id"	TEXT UNIQUE,
	PRIMARY KEY("time_id"),
	FOREIGN KEY("disaster_id") REFERENCES "natural_disasters"("Event Disaster ID") ON UPDATE CASCADE
);
CREATE TABLE IF NOT EXISTS "top_list_of_natural_disasters" (
	"Top Event ID"	TEXT NOT NULL UNIQUE,
	"Top Event Death toll (Highest estimate)"	INTEGER NOT NULL,
	"Top Event Name"	TEXT NOT NULL,
	"Top Event Location"	TEXT,
	"Top Event Date"	TEXT,
	"Top Event Note"	TEXT,
	"disaster_id"	TEXT,
	PRIMARY KEY("Top Event ID"),
	FOREIGN KEY("disaster_id") REFERENCES "natural_disasters"("Event Disaster ID") ON UPDATE CASCADE
);
COMMIT;
